head.script:
			/* 기존 localStorage 변환 */
			// /ui/works/workspace.xml
			/// scwin.import_project_onclick
			scwin.__member_id__ = "";
			scwin.__workspace_name__ = "";
			scwin.__sample_project_yn__ = "";
			/// scwin.grp_list_buildproj_onclick, scwin.btn_setting_onclick
			scwin.buildPlatform = "";
			scwin.buildProjectId = "";
			scwin.workspaceId = "";
			scwin.buildProjectName = "";
			scwin.buildProjecPkId = "";
			scwin.__update_yn__ = "";
			/// scwin.btn_history_onclick
			scwin.historyPlatform = "";
			scwin.historyProjectName = "";
			scwin.historyProjectId = "";

			/// deploy_history.xml scwin.grp_list_historyproj_onclick, scwin.btn_history_onclick
			scwin.deployHistoryPlatform = "";
			scwin.deployHistoryProjectName = "";
			scwin.deployHistoryProjectId = "";

			// /ui/build/build.xml
			/// scwin.onpageload
			scwin._buildStatusFlag_ = "";
			scwin._buildFromProjSetting_ = "";

			// /ui/history/history_list.xml
			scwin.historyWorkspaceId = "";

			// /ui/manager/userManager_domain_list.xml
			scwin._role_code_id_ = "";

			// /ui/manager/userManager_workspace_detail.xml
			scwin._workspace_setting_mode_ = "";

			scwin.onpageload = function () {
				scwin.getUserInfo();

				/* 상단 좌측 메뉴 클릭시 */
				$(".btn_toggle_menu").click(function () {
					scwin.menuSlide();
				});

				/* theme 클릭시 */
				$(".btn_theme").click(function () {
					$(".wrap").toggleClass("theme2");
				});

				$(window).resize(function () { // 화면 리사이즈시
					scwin.closeMenu();
				});
			};

			// LNB닫기 함수 (화면폭이 1280 이하일 때 LNB 자동닫기)
			scwin.closeMenu = function () {
				const winWidth = $(window).width();
				if (winWidth > 360 && winWidth < 778) {
					$(".wrap").removeClass("show_menu");
				}
			};

			/* admin(관리자) 클릭시 */
			scwin.anchor1_onclick = function (e) {
				if (anchor1.hasClass("on")) {
					anchor1.removeClass("on");
					layer03.removeClass("on");
				} else {
					anchor1.addClass("on");
					layer03.addClass("on");
				}
			};

			// Data Reload
			scwin.allRefrashSelect = async function () {
				// //workspace data 호출
				// scwin.getWorkspaceInfo();
				// //build project data 호출
				// scwin.getBuildProjectInfo();
				// //build history data 호출
				// scwin.getBuildHistoryInfo();

				if (await scwin.getWorkspaceInfo() && await scwin.getBuildProjectInfo() && await scwin.getBuildHistoryInfo()) {
					const tabIdx = tabc_layout.selectedIndex;
					tabc_layout.deleteTab(tabIdx);
					const menu_key = scwin.convertMenuCodeToMenuKey("m0100000000");
					scwin.add_tab(menu_key);
				}
			};

			// 프로젝트생성 이후 데이터 sync
			scwin.updateWorkspaceData = async function(){
				if (await scwin.getWorkspaceInfo() && await scwin.getBuildProjectInfo() && await scwin.getBuildHistoryInfo()) {
					const items = tabc_layout.tabArr;
					const result = items.filter((item) => item.userTabID.toLowerCase() == "workspace")
					if(Array.isArray(result) && result.length > 0){
						const tabID = result[0].userTabID;
						const tabIdx = tabc_layout.getTabIndex(tabID);
						const tabWindow = tabc_layout.getWindow(tabIdx);
						tabWindow.scwin.onpageload();
					}
				}
			};

			/* 사용자 정보 Get */
			scwin.getUserInfo = async function () {
				const url = common.uri.userInfo;
				const method = "GET";
				const headers = {"Content-Type": "application/json"};

				try {
					const response = await common.http.fetchGet(url, method, headers, {});
					if (response.status >= 200 && response.status < 300) {
						const data = await response.json();
						const g_user_role = data.user_role;
						await scwin.tw_menu_nodeListSettingfunc(g_user_role);

						__account_info__.setJSON([data]);

						btnUserSelect.setValue(data.user_name);

						//websocket 호출
						scwin.createWebsocket();

						// //workspace data 호출
						// await scwin.getWorkspaceInfo();

						// //build project data 호출
						// await scwin.getBuildProjectInfo();
						// //build history data 호출
						// await scwin.getBuildHistoryInfo();
						if(await scwin.getWorkspaceInfo() && await scwin.getBuildProjectInfo() && await scwin.getBuildHistoryInfo()) {
							// 초기 Workspace 탭 추가
							const menu_key = scwin.convertMenuCodeToMenuKey("m0100000000");
							scwin.add_tab(menu_key);
						} else {
							$p.url("/login.xml");
						}
					} else {
						$p.url("/login.xml");
					}
				} catch (err) {
					$p.url("/login.xml");
				}
			};

			/* menu 테이블에서, user_role (USER, ADMIN, SUPERADMIN)과 PROFILES에 따라서, 해당되는 메뉴를 가져온다. */
			scwin.tw_menu_nodeListSettingfunc = async function (user_role) {
				const url = common.uri.menuList;
				const method = "POST";
				const headers = {"Content-Type": "application/json; charset=UTF-8"};
				const body = {
					"menu_role_type": user_role,
					"menu_profile_type": g_config.PROFILES,
					"menu_lang_type": common.getLocale()
				};

				await common.http.fetch(url, method, headers, body)
						.then(async (res) => {
							await scwin.menuSetting(res);
						});
			};

			scwin.menuSetting = function (menuData) {
				dlt_side_user_List.removeAll();
				dlt_side_user_List.setJSON(menuData, true);

				let submenu_index = 0;
				for (let i = 0; i < dlt_side_user_List.getDataLength(); i++) {
					const menu_data = dlt_side_user_List.getRowData(i);
					const label_data = menu_data[0];

					if (menu_data[2] === 1) {
						gen_menu.insertChild();
						gen_menu.getChild(gen_menu.getLength() - 1, "menu_label").setValue(label_data);
						submenu_index = 0;
					} else {
						const current_gen_menu_index = gen_menu.getLength() - 1;
						const submenu_label_id = "gen_menu_" + current_gen_menu_index + "_submenu_label";
						const gen_submenu = gen_menu.getChild(current_gen_menu_index, "gen_submenu");
						gen_submenu.insertChild();
						gen_submenu.getChild(submenu_index, submenu_label_id).setValue(label_data);
						submenu_index++;
					}

					// 메뉴가 Guide 까지만 표시될 수 있도록 변경
					if (label_data.toLowerCase() === "guide") {
						break;
					}
				}

				/* snb 메뉴 클릭시 */
				$('.sidebox .side_item a').click(function () {
					if ($(this).parent().hasClass('on') == false) {
						$(this).parent().addClass("on").siblings().removeClass("on");
					}

					if (!!$(this).siblings('.dep2')[0]) {
						if ($(this).siblings('.dep2')[0].children.length == 0) {
							scwin.menuSlide();
						} else {
							if (!$(".wrap").hasClass("show_menu")) {
								$(".wrap").addClass("show_menu");
							}
							$(".btn_toggle_menu").addClass("on");
							$(".dim").addClass("on");
						}
					} else {
						scwin.menuSlide();
					}

					scwin.add_tab(null, this);
				});
			};

			scwin.menuSlide = function () {
				if (!$(".wrap").hasClass("show_menu")) {
					$(".wrap").addClass("show_menu");
				} else {
					$(".wrap").toggleClass("show_menu");
				}
				$(".btn_toggle_menu").toggleClass("on");
				$(".dim").toggleClass("on");
			};

			// menuCode를 받아 메뉴키를 반환한다.
			scwin.convertMenuCodeToMenuKey = function (menuCode) {
				const menuRow = dlt_side_user_List.getMatchedJSON("menu_code", menuCode);
				if (Array.isArray(menuRow) && menuRow.length > 0) {
					return menuRow[0].menu_key;
				}
				return null;
			};

			// menuKey를 받아 menu_page_name을 반환한다. (tab_id용도)
			scwin.convertMenuKeyToMenuPageName = function (menuKey) {
				const menuRow = dlt_side_user_List.getMatchedJSON("menu_key", menuKey);
				if (Array.isArray(menuRow) && menuRow.length > 0) {
					return menuRow[0].menu_page_name;
				}
				return menuKey;
			};

			// menuCode를 받아 url을 반환한다.
			scwin.convertMenuCodeToPath = function (menuCode) {
				const menuRow = dlt_side_user_List.getMatchedJSON("menu_code", menuCode);
				if (Array.isArray(menuRow) && menuRow.length > 0) {
					return menuRow[0].url;
				}
				return null;
			};

			scwin.add_tab = async function (tab_id_input, _this, dataObj) {
				let tab_label = ""
				if(!!_this && typeof _this.getInnerHTML == "undefined") {
                    // iOS Safari 브라우저에 getInnerHTML이 없음
					tab_label = !!_this ? _this.textContent : tab_id_input;
				} else {
					tab_label = !!_this ? _this.getInnerHTML() : tab_id_input;
				}

				const tab_opt = {
					'label': tab_label,
					'openAction': 'exist'
				}
				let tab_src = '';

				const willMovePage = dlt_side_user_List.getMatchedJSON("menu_key", tab_label);
				if (Array.isArray(willMovePage) && willMovePage.length > 0) {
					tab_src = willMovePage[0].url;
				}

                // 가이드를 클릭하면, 가이드를 열어주고 따로 탭은 열지 않는다.
				if (tab_label.toLowerCase() == "guide") {
					window.open(willMovePage[0].url);
					return;
				}

				const tab_cont = {
					'src': tab_src,
					'wframe': true,
					'scope': true,
					'dataObject': {
						'type': 'json',
						'name': 'tabParam',
						'data': dataObj ? dataObj : null
					}
				}

				if (tab_src != '') {
					const tab_id = scwin.convertMenuKeyToMenuPageName(tab_label);
					await tabc_layout.addTab(tab_id, tab_opt, tab_cont);
					const tab_index = tabc_layout.getTabIndex(tab_id);
					tabc_layout.setSelectedTabIndex(tab_index);
				}
			};

			scwin.getWorkspaceInfo = async function () {
				const url = common.uri.getWorkspaceInfo;
				const method = "GET";
				const headers = {"Content-Type": "application/json"}
				try {
					const response = await common.http.fetchGet(url, method, headers, {});
					if (response.status >= 200 && response.status < 300) {
						const data = await response.json();
						let workspace = [];

						for (const row in data) {
							let temp = {};

							temp["workspace_name"] = data[row].workspace_name;
							temp["id"] = data[row].workspace_id;
							temp["member_id"] = data[row].member_id;
							temp["favorite_flag"] = data[row].favorite_flag;
							temp["delete_yn"] = data[row].delete_yn;
							temp["workspace_group_role_id"] = !!data[row].workspace_group_role_id ? data[row].workspace_group_role_id : "";

							workspace.push(temp);
						}
						__workspace_data__.removeAll();
						let distinct = common.unique(workspace, 'id');
						__workspace_data__.setJSON(distinct, true);

						return true;
					} else {
						common.win.alert("code:" + response.status + "\n" + "message:" + response.responseText + "\n");
						return false;
					}
				} catch(err) {
					common.win.alert("code:" + err.status + "\n" + "message:" + err.responseText + "\n");
					return false;
				}
			};

			// websocket 연결 function
			scwin.createWebsocket = function () {
				const userData = __account_info__.getRowJSON(0);
				common.ws.connect(userData);
			};

			scwin.getBuildProjectInfo = async function () {
				try {
					const url = common.uri.buildProjectInfo;
					const headers = {"Content-Type": "application/json"};
					const res = await common.http.fetchPost(url, headers, {});
					if (res.status >= 200 && res.status < 300) {
						const data = await res.json();
						if (data != null) {
							let buildproj = data;
							__buildproject_data__.removeAll();
							const distinct = common.unique(buildproj, 'project_id');
							__buildproject_data__.setJSON(distinct, true);
							return true;
						} else {
							return false;
						}
					} else {
						common.win.alert("code:" + res.status + "\n" + "message:" + res.responseText + "\n");
						return false;
					}
				} catch (err){
					common.win.alert("code:" + err.status + "\n" + "message:" + err.responseText);
					return false;
				}
			};

			scwin.getBuildHistoryInfo = async function () {
				try {
					const url = common.uri.getHistoryInfo;
					const headers = {"Content-Type": "application/json"};
					const res = await common.http.fetchPost(url, headers, {});
					if (res.status >= 200 && res.status < 300) {
						const data = await res.json();
						if (data != null) {
							let buildHistory = data;
							__buildhistory_data__.removeAll();
							const distinct = common.unique(buildHistory, 'project_history_id');
							__buildhistory_data__.setJSON(distinct, true);
							return true;
						} else {
							return false;
						}
					} else {
						return false
					}
				} catch (err) {
					common.win.alert("code: " + err.status + "\n" + "message: " + err.responseText);
					return false;
				}
			};

			scwin.logoutUpdateLastLoginDate = function () {
				const url = common.uri.logout;
				const method = "GET";
				const headers = {"Content-Type": "application/json"};

				common.http.fetchGet(url, method, headers, {})
					.then(res => {
						if (res.status >= 200 && res.status < 300) {
							if (g_config.PROFILES == "service") {
								sessionStorage.removeItem("accessToken");
								$p.url("/index.html");
							} else {
								sessionStorage.removeItem("accessToken");
								$p.url("/index.html");
							}
						}
					})
					.catch(err => {
						common.win.alert("code: " + response.status + "\n" + "message: " + response.responseText);
					});
			};

			scwin.btnUserProfile_onclick = function () {

				// logout action 추가
				let whive_session = __account_info__.getRowJSON(0);
				let user_setting_mode = "detailviewuser";
				let domain_id = whive_session.domain_id;

				let data = {};
				data.domain_id = domain_id;
				data.admin_setting_mode = user_setting_mode;

				const menuKey = scwin.convertMenuCodeToMenuKey("m0404010000");
