head.script:
	scwin.onpageload = function() {
		common.setScopeObj(scwin);
		scwin.initPage();
	};

	scwin.onpageunload = function() {

	};

	scwin.initPage = function(){
		scwin.getBranchListInfo();

		scwin.getVCSListInfo();

		scwin.getFTPListInfo();

		scwin.getSigningKeyInfo();

		var settingPageViewData = $p.parent().wfm_main.getUserData("settingsData");

		if(settingPageViewData == "builder"){

			vcs_view.hide();
			FTP_view.hide();
			signingkey_view.hide();

		}else if(settingPageViewData == "vcs"){
			builder_view.hide();
			FTP_view.hide();
			signingkey_view.hide();

		}else if(settingPageViewData == "FTP"){
			builder_view.hide();
			vcs_view.hide();
			signingkey_view.hide();

		}else if(settingPageViewData == "signingkey"){
			builder_view.hide();
			vcs_view.hide();
			FTP_view.hide();
		}

	}

	scwin.getBranchListInfo = function (){
		var options = {};

		options.action = "/manager/branchSetting/getAll";
		options.mode = "asynchronous";
		options.mediatype = "application/json";
		options.method = "GET";

		options.success = function (e) {
			var data = e.responseJSON;
			if (data != null) {

				var settingBranch = [];

				for (var row in data) {
					var temp = {};

					temp["branch_id"] = data[row].builder_id;
					temp["role_code_id"] = data[row].role_code_id;
					temp["branch_user_id"] = data[row].builder_user_id;
					temp["branch_name"]	= data[row].builder_name;
					temp["session_status"] = data[row].session_status;
					temp["session_type"] = data[row].session_type;
					temp["created_date"]= data[row].created_date;
					temp["updated_date"]= data[row].updated_date;

					settingBranch.push(temp);

				}

				var distict = common.unique(settingBranch, 'branch_id');
				// __workspace_data__.setJSON(distict);
				__branch_setting_data__.setJSON(distict);
				var branch_setting = __branch_setting_data__.getAllJSON();
				// __Sample_data__.setJSON(tempProj);

				// generator_branch_setting_list
				scwin.getBranchSettingList(branch_setting);
				// $p.parent().wfm_main.setSrc("/xml/main.xml");

			} else {
				// alert("가져오기 화면 이동 실패");
			}
		};

		options.error = function (e) {
			alert("code:"+request.status+"\n"+"message:"+request.responseText+"\n");

		};

		$p.ajax( options );
	};

	scwin.getBranchSettingList = function(branch_setting) {
		var count = 0;

		for(var idx in branch_setting){
			var buildbtnInx = generator_branch_setting_list.insertChild();

			var txt_proj_name = generator_branch_setting_list.getChild(count,"branch_setting_name");
			var branchSettingDetailadd = generator_branch_setting_list.getChild(count,"branch_detail_byBranchID");
			var builderSettingDetailBtn = generator_branch_setting_list.getChild(count,"btn_builder_detail"); //  btn_builder_detail

			branchSettingDetailadd.setUserData("branch_id",branch_setting[idx].branch_id);

			builderSettingDetailBtn.setUserData("branch_id",branch_setting[idx].branch_id);

			txt_proj_name.setValue(branch_setting[idx].branch_name);
			count++;

		}
	};

	scwin.getVCSListInfo = function() {
		var options = {};

		options.action = "/manager/vcs/search/profileListAll";
		options.mode = "asynchronous";
		options.mediatype = "application/json";
		options.method = "GET";

		options.success = function (e) {
			var data = e.responseJSON;
			if (data != null) {

				var settingVCS = [];

				for (var row in data) {
					var temp = {};

					temp["vcs_id"] = data[row].vcs_id;
					temp["role_code_id"] = data[row].role_code_id;
					temp["vcs_type"] = data[row].vcs_type;
					temp["vcs_user_id"] = data[row].vcs_user_id;
					temp["vcs_user_pwd"] = data[row].vcs_user_pwd;
					temp["vcs_name"]	= data[row].vcs_name;
					temp["vcs_url"]	= data[row].vcs_url;
					temp["session_status"] = data[row].session_status;
					temp["session_type"] = data[row].session_type;
					temp["created_date"]= data[row].created_date;
					temp["updated_date"]= data[row].updated_date;
					temp["admin_id"] = data[row].admin_id;

					settingVCS.push(temp);

				}

				var distict = common.unique(settingVCS, 'vcs_id');
				// __workspace_data__.setJSON(distict);
				__vcs_setting_data__.setJSON(distict);
				var vcs_setting = __vcs_setting_data__.getAllJSON();
				// __Sample_data__.setJSON(tempProj);

				// generator_branch_setting_list
				scwin.getVCSSettingList(vcs_setting);
				// $p.parent().wfm_main.setSrc("/xml/main.xml");

			} else {
				// alert("가져오기 화면 이동 실패");
			}
		};

		options.error = function (e) {
			alert("code:"+request.status+"\n"+"message:"+request.responseText+"\n");

		};

		$p.ajax( options );
	};


	scwin.getVCSSettingList = function(vcs_setting) {
		var count = 0;

		for(var idx in vcs_setting){
			   // super admin
				var buildbtnInx = generator_vcs_setting_list.insertChild();
				var txt_vcs_setting_name = generator_vcs_setting_list.getChild(count,"vcs_setting_name");
				var vcsSettingDetailadd = generator_vcs_setting_list.getChild(count,"vcs_detail_byVCSID");
				var vcsSettingType = generator_vcs_setting_list.getChild(count,"vcs_setting_type");

				vcsSettingDetailadd.setUserData("vcs_id",vcs_setting[idx].vcs_id);

				if(vcs_setting[idx].vcs_type == "git"){
					var label = common.getLabel("lbl_git");
					vcsSettingType.setValue(label);
				}else if(vcs_setting[idx].vcs_type == "localgit"){
					var label = common.getLabel("lbl_settings_repo");
					vcsSettingType.setValue(label);
				}else if(vcs_setting[idx].vcs_type == "svn"){
					var label = common.getLabel("lbl_settings_svn");
					vcsSettingType.setValue(label);
				}

				txt_vcs_setting_name.setValue(vcs_setting[idx].vcs_name);
				count++;


		}
	};

