	scwin.getFTPSettingList = function(ftp_setting) {
		var count = 0;

		for(var idx in ftp_setting){
			var buildbtnInx = generator_ftp_setting_list.insertChild();

			var txt_ftp_setting_name = generator_ftp_setting_list.getChild(count,"ftp_setting_name");
			var ftpSettingDetailadd = generator_ftp_setting_list.getChild(count,"ftp_detail_byFTPID");

			ftpSettingDetailadd.setUserData("ftp_id",ftp_setting[idx].ftp_id);

			txt_ftp_setting_name.setValue(ftp_setting[idx].ftp_name);
			count++;

		}
	};

	scwin.getSigningKeyInfo = function(){

	    // role code 가 admin 일 경우
		var options = {};

		options.action = "/manager/mCert/common/selectAll";
		options.mode = "asynchronous";
		options.mediatype = "application/json";
		options.method = "POST";

		options.success = function (e) {
			var data = e.responseJSON;
			if (data != null) {

				var settingSigningFileList = [];

				for (var row in data) {
					var temp = {};

					temp["key_id"] = data[row].key_id;
					temp["key_name"] = data[row].key_name;
                    temp["key_type"] = data[row].key_type;
					temp["platform"] = data[row].platform;
					temp["created_date"] = data[row].created_date;
					temp["updated_date"] = data[row].updated_date;

					settingSigningFileList.push(temp);

				}

				var distict = common.unique(settingSigningFileList, 'key_id');

				__key_setting_data__.setJSON(distict);
				var key_setting = __key_setting_data__.getAllJSON();


				scwin.getKeySettingList(key_setting);

			} else {
				// alert("가져오기 화면 이동 실패");
			}
		};

		options.error = function (e) {
			alert("code:"+e.status+"\n"+"message:"+e.responseText+"\n");

		};

		$p.ajax( options );

	};

	scwin.getKeySettingList = function(key_setting){

		var count = 0;

		for(var idx in key_setting){
			var buildbtnInx = generator_signingkey_setting_list.insertChild();

			var txt_key_setting_name = generator_signingkey_setting_list.getChild(count,"key_setting_name");
			var spen_key_setting_platform = generator_signingkey_setting_list.getChild(count,"key_setting_platfrom");
			var keySettingDetailadd = generator_signingkey_setting_list.getChild(count,"detail_byKeyID");
            var txt_key_setting_type = generator_signingkey_setting_list.getChild(count,"key_setting_type");

			keySettingDetailadd.setUserData("key_id",key_setting[idx].key_id);
			keySettingDetailadd.setUserData("platform",key_setting[idx].platform);

            if(key_setting[idx].platform == "Android"){
				spen_key_setting_platform.addClass("ico_and");
			}else if(key_setting[idx].platform == "iOS"){
				spen_key_setting_platform.addClass("ico_ios");
			}

			if(key_setting[idx].key_type == "build"){
				var label = common.getLabel("lbl_settings_dev");
				txt_key_setting_type.setValue(label);

			}else if(key_setting[idx].key_type == "deploy"){
				var label = common.getLabel("lbl_operate");
				txt_key_setting_type.setValue(label);
			}



			txt_key_setting_name.setValue(key_setting[idx].key_name);
			count++;

		}


	};

	scwin.grp_list_detail_onclick = function(){

		var controlList = this.parentControl.parentControl.childControlList;
		for(var idx in controlList){
			if(controlList[idx] === this.parentControl){
				this.parentControl.toggleClass("on");
			} else {
				controlList[idx].removeClass("on");
			}
		}

	};

	scwin.grp_list_branchdetail_byBranchID_onclick = function(e){

		var branch_id = this.getUserData("branch_id");

		localStorage.setItem("_branch_id_",branch_id);
		localStorage.setItem("_branch_setting_mode_","detailview"); // or detailcreate

		// var datacheck = e.id.getAttributeNode("txt_buildproj_pkid");
		// localStorage.setItem("build_project_pk_id",platform);
		$p.parent().wfm_main.setSrc("/xml/setting_branch_detail.xml");

	};

	scwin.create_branchSetting_onclick = function(){

		localStorage.setItem("_branch_setting_mode_","detailcreate");
		$p.parent().wfm_main.setSrc("/xml/setting_branch_detail.xml");
	};

