head.script:
	scwin.onpageload = function() {
		common.setScopeObj(scwin);
		scwin.initPage();
	};

	scwin.onpageunload = function() {

	};

	scwin.initPage = function(){
		scwin.getMemberListInfo();

		scwin.getWorkspaceListInfo();

		scwin.getDomainListInfo();

		scwin.getRoleListInfo();

		var roleViewData = $p.parent().wfm_main.getUserData("roleViewData");

		if(roleViewData == "domain"){
			workspace_role_view.hide();
			role_view.hide();
			user_list_view.hide();

		}else if(roleViewData == "workspaceRoleView"){
			domain_view.hide();
			role_view.hide();
			user_list_view.hide();

		}else if(roleViewData == "roleView"){
			domain_view.hide();
			workspace_role_view.hide();
			user_list_view.hide();

		}else if(roleViewData == "userListView"){

			if (g_config.PROFILES == "service") {
			    create_userSetting_btn_id.hide();
			}else {
				create_userSetting_btn_id.show();
			}

			domain_view.hide();
			workspace_role_view.hide();
			role_view.hide();
		}

	};

	// 유저 전체 리스트 조회 ajax
	scwin.getMemberListInfo = function (){
		var options = {};

		// /api/members
		options.action = "/manager/member/search/userListAll";
		options.mode = "asynchronous";
		options.mediatype = "application/json";
		options.method = "GET";

		options.success = function (e) {
			var data = e.responseJSON;
			if (data != null) {

				var managerAdmin = [];

				for (var row in data) {
					var temp = {};

					temp["user_id"] = data[row].user_id;
					temp["email"] = data[row].email;
					temp["domain_id"] = data[row].domain_id;
					temp["user_role"] = data[row].user_role;
					temp["user_name"]	= data[row].user_name;
					temp["created_date"]= data[row].created_date;
					temp["updated_date"]= data[row].updated_date;
					temp["last_login_date"]= data[row].last_login_date;

					managerAdmin.push(temp);

				}

				var distict = common.unique(managerAdmin, 'user_id');
				__member_manager_data__.setJSON(distict);
				var member_manager = __member_manager_data__.getAllJSON();
				// console.log(member_manager);
				// generator_branch_setting_list
				scwin.getMemberSettingList(member_manager);

			} else {
				// alert("가져오기 화면 이동 실패");
			}
		};

		options.error = function (e) {
			alert("message:"+request.responseText+"\n");

		};

		$p.ajax( options );
	};

	// test
	scwin.getMemberSettingList = function(member_manager) {
		var count = 0;

		for(var idx in member_manager){
			var buildbtnInx = generator_member_manager_list.insertChild();

			var txt_member_name = generator_member_manager_list.getChild(count,"member_setting_name");
			var memberDetailadd = generator_member_manager_list.getChild(count,"member_detail_byMemberID");
			var memberDetailSettingAdd = generator_member_manager_list.getChild(count,"btn_member_detail_setting");

			memberDetailadd.setUserData("member_id",member_manager[idx].user_id);
			memberDetailadd.setUserData("domain_id",member_manager[idx].domain_id);

			memberDetailSettingAdd.setUserData("member_id",member_manager[idx].user_id);
			memberDetailSettingAdd.setUserData("domain_id",member_manager[idx].domain_id);

			txt_member_name.setValue(member_manager[idx].user_name);
			count++;

		}
	};

	// Workspace 권한 설정 조회 ajax
	scwin.getWorkspaceListInfo = function(){
		var options = {};

		// /manager/workspace/search/workspaceAll
		// options.action = "/manager/member/search/userListAll";
		options.action = "/manager/workspace/search/workspaceAll";
		options.mode = "asynchronous";
		options.mediatype = "application/json";
		options.method = "GET";

		options.success = function (e) {
			var data = e.responseJSON;
			if (data != null) {

				var workspaceList = [];

				for (var row in data) {
					var temp = {};

					temp["id"] = data[row].id;
					temp["workspace_name"] = data[row].workspace_name;
					temp["member_id"] = data[row].member_id;
					temp["status"] = data[row].status;
					temp["favorite_flag"] = data[row].favorite_flag;
					temp["status"] = data[row].status;

					workspaceList.push(temp);

				}

				var distict = common.unique(workspaceList, 'id');
				// __workspace_data__.setJSON(distict);
				__workspace_setting_data__.setJSON(distict);
				var workspcae_manager = __workspace_setting_data__.getAllJSON();
				// __Sample_data__.setJSON(tempProj);
				// console.log(workspcae_manager);
				// generator_branch_setting_list
				//scwin.getWorkspaceSettingList(workspcae_manager);

			} else {
				// alert("가져오기 화면 이동 실패");
			}
		};

		options.error = function (e) {
			alert("message:"+request.responseText+"\n");

		};

		$p.ajax( options );
	};

	scwin.getWorkspaceSettingList = function(workspcae_manager) {
		var count = 0;

		for(var idx in workspcae_manager){
			var buildbtnInx = generator_workspace_setting_list.insertChild();
			var txt_workspace_name = generator_workspace_setting_list.getChild(count,"workspcae_setting_name");
			var workspaceDetailadd = generator_workspace_setting_list.getChild(count,"workspcae_detail_byWorkspcaeID");
			var workspaceDetailSettingAdd = generator_workspace_setting_list.getChild(count,"btn_workspace_detail_setting");

			// workspace detail view : workspace_id, workspace_name, role_code_name param 전달
			workspaceDetailadd.setUserData("workspace_id",workspcae_manager[idx].id);
			workspaceDetailadd.setUserData("workspace_name",workspcae_manager[idx].workspace_name);
			workspaceDetailadd.setUserData("member_role",workspcae_manager[idx].status); // member role

			workspaceDetailSettingAdd.setUserData("workspace_id",workspcae_manager[idx].id);
			workspaceDetailSettingAdd.setUserData("workspace_name",workspcae_manager[idx].workspace_name);
			workspaceDetailSettingAdd.setUserData("member_role",workspcae_manager[idx].status); // member role

			txt_workspace_name.setValue(workspcae_manager[idx].workspace_name);
			count++;

		}
	};

