	scwin.grp_list_detail_onclick = function(){

		var controlList = this.parentControl.parentControl.childControlList;
		for(var idx in controlList){
			if(controlList[idx] === this.parentControl){
				this.parentControl.toggleClass("on");
			} else {
				controlList[idx].removeClass("on");
			}
		}

	};

	scwin.grp_user_role_onclick = function(){

		$p.parent().wfm_main.setUserData("_member_setting_mode_","detailcreate");
		// localStorage.setItem("_member_setting_mode_","detailcreate"); // or detailview

		$p.parent().wfm_main.setSrc("/xml/userManager_user_role_detail.xml");

	};

