head.script:
			scwin.build_project_name_yn = false;
			scwin.platformType = "";
			scwin.paramData = {};

			scwin.onpageload = function () {

				scwin.paramData = $p.getParameter("wframeParam");

				let uri = common.uri.platformList;
				common.http.fetchGet(uri, "GET", {"Content-Type": "application/json"}).then(async (res) => {
					const data = await res.json()
					dlt_platform_selectbox.setJSON(data);

					project_import_step1_platform_select.setSelectedIndex(0);

					scwin.platformType = project_import_step1_platform_select.getValue();

                    let message;
					if(scwin.platformType == "Android"){
                        message = common.getLabel("lbl_package_name");
					} else {
                        message = common.getLabel("lbl_xcode_project_name");
					}
                    txt_package_name.setValue(message);

					// 디바이스, 언어 선택
					scwin.getProgrammingInfo(scwin.platformType);

					// Builder List 조회
					scwin.getBranchSettingInfo();

					// vcs 프로필조회
					// 최초 일반앱 기준으로 git만 활성화
					scwin.getVCSSettingFindAdminAndTypeInfo("git");

					// 배포서버 프로필조회
					scwin.getFTPSettingInfo();

					// 인증서 프로필 조회
					scwin.getSigningKeySettingInfoToAdmin(scwin.platformType);

				}).catch(e => {
					common.win.alert("platform code:" + e.status + "\n" + "message:" + e.responseJSON + "\n");
				})


			};

			/**
			 * scwin.getProgrammingInfo (platform)
			 * platform : Android/iOS
			 *
			 * 플랫폼 별 사용 언어 조회 기능
			 *
			 */
			scwin.getProgrammingInfo = function (platform) {
				//project_import_step1_platform_language_select
				const uri = common.uri.programLanguageList(platform);
				common.http.fetchGet(uri,"GET",{"Content-Type":"application/json"}).then((res)=>{
					return res.json();
				}).then((data)=>{
					if (data != null) {
						dlt_language_selectbox.setJSON(data);
					}
				}).catch((err)=>{
					common.win.alert("error status"+err.status+", message:"+err.message);
				});
			};

			/**
			 *  scwin.getBranchSettingInfo
			 *  Builder Profile 리스트 조회 function
			 */
			scwin.getBranchSettingInfo = function () {
				const uri = common.uri.getBuilderList;
				common.http.fetchGet(uri,"GET",{"Content-Type":"application/json"}).then((res)=>{
					return res.json();
				}).then((data)=>{
					dlt_builder_setting_selectbox.setJSON(data);
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			/**
			 *  scwin.getVCSSettingFindAdminAndTypeInfo
			 *  VCS Profile 리스트 조회 function
			 */
			scwin.getVCSSettingFindAdminAndTypeInfo = function(vcs_type){
				const uri = common.uri.vscList(vcs_type);
				common.http.fetchGet(uri,"GET",{"Content-Type":"application/json"}).then((res)=>{
					return res.json();
				}).then((data)=>{
					dlt_vcs_setting_selectbox.setJSON(data);
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			/**
			 *  scwin.getFTPSettingInfo
			 *  내부 배포 서버  Profile 리스트 조회 function
			 */
			scwin.getFTPSettingInfo = function () {
				const uri = common.uri.ftpList;
				common.http.fetchGet(uri,"GET",{"Content-Type":"application/json"}).then((res)=>{
					return res.json();
				}).then((data)=>{
					dlt_ftp_setting_selectbox.setJSON(data);
				}).catch((err)=>{
					common.win.alert("error status"+err.status+", message:"+err.message);
				});
			};

			/**
			 *  scwin.getSigningKeySettingInfoToAdmin
			 *  인증서 Profile 리스트 조회 function
			 */
			scwin.getSigningKeySettingInfoToAdmin = function (platform) {
				let datareq = {};
				datareq.platform = platform;
				datareq.sampleProjectYn = "N";

				const uri = common.uri.signingProfile;
				common.http.fetchPost(uri,{"Content-Type":"application/json"},datareq).then((res)=>{
					return res.json();
				}).then((data)=>{
					dlt_signing_profile_selectbox.setJSON(data);
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			scwin.checkData = function () {
				const project_name = project_import_project_name.getValue();
				const platform = project_import_step1_platform_select.getValue();
				const builderProfile = project_import_step1_builder_select.getValue();

				const project_name_check_str = common.checkAllInputText("CHECK_INPUT_TYPE_PROJECT_NAME", project_name);

                let message;
				if (common.isEmptyStr(project_name)) {
                    message = common.getLabel("lbl_check_project_name");
					common.win.alert(message);
					return false;
				}

				if (!project_name_check_str) {
                    message = common.getLabel("lbl_project_add_step01_js_check_project_name");
					common.win.alert(message);
					return false;
				}

				if (!builderProfile) {
                    message = common.getLabel("lbl_project_add_step01_js_select_builder_setting");
					common.win.alert(message);
					return false;
				}

				if (common.isEmptyStr(platform)) {
                    message = common.getLabel("lbl_project_add_step01_js_select_platform_type");
					common.win.alert(message);
					return false;
				}

				return true;
			};

			scwin.saveStep1Data = function () {
				let data = {};
				data.workspace_id = scwin.paramData.workspace_id;
				data.project_name = project_import_project_name.getValue();
				data.product_type = project_import_step1_product_type_select.getValue();
				data.platform = project_import_step1_platform_select.getValue();
				data.platform_language = project_import_step1_platform_language_select.getText();
				data.project_dir_path = "";
				data.description = project_import_step1_txtarea_desc.getValue();
				data.status = 1; // build project 사용여부 옵션 기능 추가시 필요.
				data.builder_id =  project_import_step1_builder_select.getValue();
				data.vcs_id = project_import_step1_vcs_select.getValue();
				data.ftp_id = project_import_step1_ftp_select.getValue();
				data.key_id = project_import_step1_signingKey_select.getValue();

				dtl_build_import_project_step1.setJSON([data]);

				scwin.setTemplateProjectCopy(data);
			};

			scwin.setBuilderToProjectImportStastus = async function(data) {
				switch (data.gitStatus) {
					case "DONE" :
						WebSquare.layer.hideProcessMessage();
						scwin.btn_project_step4_checkYn = true;
						scwin.createProjectToFlag_yn = true;
						$p.getComponentById("btn_import_step01").setDisabled(true);

						// project import 이후 workspace 화면 갱신
						$p.top().scwin.updateWorkspaceData();

						// set project id
						dtl_build_import_project_step1.setCellData(0,"project_id",data.build_id);

						message = common.getLabel("lbl_project_import_js_confirm");
						if (await common.win.confirm(message)){
							const buildproj_json = dtl_build_import_project_step1.getRowJSON(0);

							let buildAction = [];
							let data = {};
							data.platform = buildproj_json.platform;
							data.projectName = buildproj_json.project_name;
							data.project_pkid = buildproj_json.project_id;
							data.workspace_pkid =buildproj_json.workspace_id;
							data.product_type = buildproj_json.product_type;
							buildAction.push(data);

							$p.parent().$p.parent().__buildaction_data__.setJSON(buildAction);
							// /ui/build/build.xml
							const tabId = $p.top().scwin.convertMenuCodeToMenuKey("m0101000000");
							$p.top().scwin.add_tab(tabId);
						}
						break;
					case "FAILD" :
						WebSquare.layer.hideProcessMessage();
                        message = common.getLabel("lbl_failed_project_import");
						common.win.alert(message);
						break;
					default :
						const statusMsg = data.gitStatus ? data.gitStatus : "";
						WebSquare.layer.showProcessMessage(statusMsg);
						break;
				}
			};

			scwin.setBuilderDeploySettingStatus = function (obj) {
				switch (obj.status) {
					case "DONE":
						WebSquare.layer.hideProcessMessage();
						break;
					default :
						const statusMsg = obj.status ? obj.status : "";
						WebSquare.layer.showProcessMessage(statusMsg);
						break;
				}
			};

			scwin.setBuildSetting = function (buildproj_json, buildsetting_json) {
				let build_all_in_json = {};
				build_all_in_json.workspace_group_role_id = scwin.paramData.workspace_group_role_id
				build_all_in_json.buildProject = buildproj_json;
				build_all_in_json.buildSetting = buildsetting_json;

				const uri = common.uri.projectImportVcsMultiProfiles;
				common.http.fetchPost(uri,{"Content-Type":"application/json"},build_all_in_json).then((res)=>{
					return res.json();
				}).then((data)=>{
					const build_id = data[0].build_id;
					$p.parent().dtl_build_import_project_step1.setCellData(0, "project_id", build_id);
					btn_import_step01.setDisabled(true);
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			scwin.setTemplateProjectCopy = function (buildproj_json) {
				let import_all_in_json = {};
				import_all_in_json.workspace_group_role_id = scwin.paramData.workspace_group_role_id ? scwin.paramData.workspace_group_role_id : "";
				import_all_in_json.packageName = ibx_package_name.getValue();
				import_all_in_json.buildProject = buildproj_json;
				import_all_in_json.buildSetting = "";

				const uploader = document.getElementById("zipFile");
				let importProjectZipFile = uploader.files[0];
				let formData = new FormData();
				formData.append("zipFile", importProjectZipFile);
				formData.append('projectImportJson', JSON.stringify(import_all_in_json));
				const uri = common.uri.importMultiProfileTemplateProject;

				common.http.fetchUpload(uri,"POST",{"Accept":"application/json,*/*"},formData).then((res)=>{
					return res.json();
				}).then((data)=>{
					//scwin.signingkeyLastCnt = data[0].signingkeyCnt;
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			// product type onchange event
			scwin.project_import_step1_product_type_select_onchange = function(info){
				const product = info.newValue;
				const platform = project_import_step1_platform_select.getValue();

				if (product.toLowerCase() == "wmatrix") {
					grp_language.setStyle("display", "table-row");

					scwin.getProgrammingInfo(platform);
					scwin.getBranchSettingInfo();
					scwin.getFTPSettingInfo();

					const builder_profile = project_import_step1_builder_select.getValue();
					scwin.getSigningKeySettingInfoToAdmin(platform);

					if (!!builder_profile) {
						scwin.getVCSSettingFindAdminAndTypeInfo("localgit,localsvn");
					} else {
						scwin.getVCSSettingFindAdminAndTypeInfo("git");
					}

				}
				// 일반 앱인 경우
				else {
					grp_language.setStyle("display", "none");
					// 일반 앱 가져오기는 git, localgit만 지원한다. (SVN 지원 X)
					scwin.getVCSSettingFindAdminAndTypeInfo("git,localgit");
				}
			};

			// platform select onchange event
			scwin.project_import_step1_platform_select_onchange = function (info) {
				const platform = info.newValue;
				const builder_profile = project_import_step1_builder_select.getValue();
				const product = project_import_step1_product_type_select.getValue();

				scwin.getProgrammingInfo(platform);
				scwin.getBranchSettingInfo();
				scwin.getFTPSettingInfo();

                let message;
				if (platform == "Android") {
                    message = common.getLabel("lbl_package_name");
				} else {
                    message = common.getLabel("lbl_xcode_project_name");
				}
                txt_package_name.setValue(message);
				scwin.getSigningKeySettingInfoToAdmin(platform);

				if (!!builder_profile) {
					if(product.toLowerCase() == "wmatrix") {
						scwin.getVCSSettingFindAdminAndTypeInfo("localgit,localsvn");
					} else {
						scwin.getVCSSettingFindAdminAndTypeInfo("git,localgit");
					}
				} else {
					scwin.getVCSSettingFindAdminAndTypeInfo("git");
				}
			};

			// builder select onchage
			scwin.project_import_step1_builder_select_onchange = function (info) {
				const platform = project_import_step1_platform_select.getValue();
				const product = project_import_step1_product_type_select.getValue();
				if (info.newValue != "") {
					scwin.getSigningKeySettingInfoToAdmin(platform);

					if(product.toLowerCase() == "wmatrix"){
						scwin.getVCSSettingFindAdminAndTypeInfo("localgit,localsvn");
					} else {
						scwin.getVCSSettingFindAdminAndTypeInfo("git");
					}
				} else {
					scwin.getVCSSettingFindAdminAndTypeInfo("git");
				}
			};

			// 프로젝트 이름확인
			scwin.project_import_step1_btn_check_project_name_onclick = function (e) {
				const project_name = project_import_project_name.getValue();
				if (scwin.checkDataProjectName(project_name)) {
					scwin.select_check_project_name(project_name);
				}
			};

			// 프로젝트 이름 정합성체크
			scwin.checkDataProjectName = function (project_name) {
				const project_name_check_str = common.checkAllInputText("CHECK_INPUT_TYPE_PROJECT_NAME", project_name);
				if (common.isEmptyStr(project_name)) {
                    const message = common.getLabel("lbl_check_project_name");
					common.win.alert(message);
					return false;
				}

				if(common.checkAllInputText("CHECK_INPUT_TYPE_SPC",project_name)){
                    const message = common.getLabel("lbl_project_name_special_char_rule");
					common.win.alert(message);
					return false;
				}

				if (!project_name_check_str) {
                    const message = common.getLabel("lbl_project_add_step01_js_check_project_name");
					common.win.alert(message);
					return false;
				}
				return true;
			};

			// 프로젝트이름 중복체크
			scwin.select_check_project_name = function (project_name) {
				const uri = common.uri.checkProjectName(project_name);
				common.http.fetchGet(uri,"GET",{"Content-Type":"application/json"}).then((res)=>{
					return res.json();
				}).then((data)=>{
					if(data[0].projectname_not_found == "no"){
                        const message = common.getLabel("lbl_exist_name");
						common.win.alert(message);
					}else if(data[0].projectname_not_found == "yes"){
                        const message = common.getLabel("lbl_can_use_name");
						common.win.alert(message);
						scwin.build_project_name_yn = true;
					}
				}).catch((err)=>{
					common.win.alert("error status:"+err.status+", message:"+err.message);
				});
			};

			scwin.btn_import_step01_onclick = async function (e) {
				if(!scwin.build_project_name_yn){
                    const message = common.getLabel("lbl_check_project_name");
					common.win.alert(message);
					return
				}

				if(scwin.checkData()){
                    const message = common.getLabel("lbl_do_project_import");
					if (await common.win.confirm(message)) {
						scwin.saveStep1Data();
					}
				}
			};
